<script>// Lấy tất cả các thẻ div nongtrai trên trang web
    var nongtrai = document.querySelectorAll('.nongtrai');

    // Lặp qua từng thẻ div nongtrai
    nongtrai.forEach(function (item) {
        // Tạo biến lưu trữ thẻ pop-up của từng thẻ div nongtrai
        var popup = item.querySelector('.popuptext');

        // Sự kiện mouseover để hiển thị pop-up
        item.addEventListener('mouseover', function () {
            popup.classList.remove('hide');
        });


        // Sự kiện mouseout để ẩn pop-up
        item.addEventListener('mouseout', function () {
            popup.classList.add('hide');
        });

        // Đặt vị trí của pop-up trên trang web
        popup.style.top = (item.offsetTop + item.offsetHeight) + 'px';
        popup.style.left = (item.offsetLeft + item.offsetWidth / 2 - popup.offsetWidth / 2) + 'px';

        // Thêm pop-up vào trang web
        document.body.appendChild(popup);
    });</script>
